syntax_editor_macro.text:
var gm = new GlideModal('glide_confirm_standard'); 
gm.setTitle('Popup Title');
gm.setPreference('title', 'Info message');
gm.setPreference('warning', true);
gm.setPreference('onPromptComplete', function () {
    gm.dev_okPressed = true;
});
gm.setPreference('onPromptCancel', function () {
    gm.dev_cancelPressed = true;
});
gm.on('closeconfirm', function() {
    var okPressed = !!gm.dev_okPressed;

    // reset state
    delete gm.dev_okPressed;
    delete gm.dev_cancelPressed;

    if (okPressed ) {
        // must return false to prevent closing
        // (this effectively only allows closing the popup using the OK button)
        return false;
    }
});
gm.render();
