syntax_editor_macro.text:
var gm = new GlideModal('glide_prompt'); 
gm.setTitle('Popup Title');
gm.setPreference('title', 'Please enter a value');
gm.setPreference('text', 'input default value');
gm.setPreference('onPromptComplete', function (input) {
    gm.dev_input = input;
    gm.dev_okPressed = true;
});
gm.setPreference('onPromptCancel', function (input) {
    gm.dev_input = input;
    gm.dev_cancelPressed = true;
});
gm.on('closeconfirm', function() {
    var okPressed = !!gm.dev_okPressed;
    var input = gm.dev_input;

    // reset state
    delete gm.dev_okPressed;
    delete gm.dev_cancelPressed;
    delete gm.dev_input;

    if (okPressed ) {
        // only allow closing dialog on 'OK' with some value entered
        return input.length > 0;
    }
});
gm.render();
