sys_script_client.script:
function onSubmit() {
	try {
		var config = g_scratchpad.devops_plus;
		if (config && config.add_scope_on_save && !config.is_dev_studio) {
			var action = g_form.getActionName();
			if (action && action.startsWith('sysverb_update')) {
				g_form.getFormElement().sysparm_record_scope.value = config.record_scope;
			}
		}
	} catch (e) {

	}
}
