sys_script_include.script:
/* global global, Class, GlideScriptedHierarchicalWorker */
/* eslint no-undef: "error" */
var DecryptUtilClient = Class.create();
DecryptUtilClient.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	initialize: function () {
		global.AbstractAjaxProcessor.prototype.initialize.apply(this, arguments);
	},

	decrypt: function () {
		const table = this.getParameter('table');
		const sysId = this.getParameter('sys_id');
		const field = this.getParameter('field');
		const result = {
			status: 'error',
			message: ''
		};

		if (table && sysId && field) {
			const worker = new GlideScriptedHierarchicalWorker();
			worker.setProgressName('Decrypt ' + table + ':' + sysId + ':' + field);
			worker.setScriptIncludeName('x_424426_devops.DecryptUtil');
			worker.setScriptIncludeMethod('decrypt');
			worker.putMethodArg('table', table);
			worker.putMethodArg('sysId', sysId);
			worker.putMethodArg('field', field);
			worker.setBackground(true);
			worker.start();

			result.execution_id = worker.getProgressID();
			result.status = 'success';
		}

		return JSON.stringify(result);
	},

	type: 'DecryptUtilClient'
});
