sys_script_include.script:
/* global Class, GlideRecord, GlideRecordSecure, GlideScopedEvaluator */
/* eslint no-undef: "error" */
var DevOpsExt = Class.create();
DevOpsExt.prototype = {
	initialize: function () {
		const scriptGr = new GlideRecord('clone_cleanup_script');
		scriptGr.addQuery('sys_scope', 'global');
		scriptGr.setLimit(1);
		scriptGr.query();
		if (scriptGr.next()) {
			this.scriptGr = scriptGr;
		} else {
			throw new Error('Infuseable Script Record required!');
		}
	},

	getRecord: function (tableName, sysId) {
		this.scriptGr.script = `(function (tableName, sysId) {
			var recordGr = new GlideRecordSecure(tableName);
			recordGr.addQuery('sys_id', sysId);
			recordGr.query();
			if (recordGr.next()) {
				// wrapped in an if because of healthscan
			}

			return recordGr;
		})(tableName, sysId);`;
		return new GlideScopedEvaluator().evaluateScript(this.scriptGr, 'script', {
			tableName: tableName,
			sysId: sysId
		});
	},

	getZIPEntries: function (attSysID, options) {
		this.scriptGr.script = `(function (attSysID, options) {
			var zis = new Packages.java.util.zip.ZipInputStream(new GlideSysAttachmentInputStream(attSysID));
			var entry;

			var results = [];
			while ((entry = zis.getNextEntry()) !== null) {
				var fileEntry = {
					name: entry.getName(),
					directory: entry.isDirectory()
				};

				if (!fileEntry.directory && options && options.withContent) {
					var bis = new Packages.java.io.BufferedInputStream(zis, 1024);
					var b = -1;
					fileEntry.bytes = [];
					while ((b = bis.read()) != -1) {
						fileEntry.bytes.push(b);
					}
				}

				zis.closeEntry();
				results.push(fileEntry);
			}

			return results;
		})(attSysID, JSON.parse(options));`;
		return new GlideScopedEvaluator().evaluateScript(this.scriptGr, 'script', {
			attSysID: attSysID,
			options: JSON.stringify(options || {})
		});
	},

	type: 'DevOpsExt'
};
