sys_ui_action.script:
/* global g_form, g_navigation */
/* eslint no-undef: "error" */
function openClearWindowScript() {
	const cache = g_form.getValue('cache_name');
	const scope = g_form.getValue('sys_scope');
	const bgs = g_navigation.openPopup('sys.scripts.do?noredirect=true', 'DevOps+ Clear Cache Script', 'width=1000,height=550');
	bgs.addEventListener('DOMContentLoaded', function () {
		bgs.document.getElementById('runscript').value = 
			'//sn_scoped_cache.ScopedCacheManager.flushScopedCache(\'' + cache + '\');\n'
			+ 'var content = sn_scoped_cache.ScopedCacheManager.get(\'' + cache + '\', \'key_name\');\n'
			+ 'gs.info(content);';
		bgs.setTimeout(function setScope() {
			const scopeSelector = bgs.document.querySelector('select[name="sys_scope"]');
			const options = scopeSelector && scopeSelector.querySelectorAll('option');
			for (let i = 0; options && i < options.length; i++) {
				// PDIs have the global scope option with value global
				// Customer Instances have global with SysID value
				if (options[i].value == scope) {
					scopeSelector.value = options[i].value;
					return;
				}
			}

			bgs.setTimeout(setScope, 100);
		});
	});
}
