sys_ui_page.client_script:
/* global addLoadEvent, GlideModal, Event, gel, window */
/* eslint no-undef: "error" */
addLoadEvent(function () {
	const isMac = window.navigator.platform.toUpperCase().indexOf("MAC") >= 0;
	const gm = GlideModal.get();
	const input = gel('input');
	input.value = gm.getPreference('value');
	input.addEventListener('keydown', function (e) {
		if (e.key === "Enter" && ((isMac && e.metaKey) || (!isMac && e.ctrlKey))) {
			e.preventDefault(); // prevent newline from being added to input
			gel('ok_button').focus();
		}
	});
	input.focus();
});

function onOK() {
	var gdw = GlideModal.get();
	var onComplete = gdw.getPreference('onPromptComplete');
    if (typeof onComplete == 'function') {
		try {
			var ret = onComplete.call(gdw, gel('input').value);
			if (ret === true || ret === undefined) {
				gdw.destroy();
			} else if (ret) {
				gel('error_message').innerHTML = ret;
			}
		} catch (e) {
			window.console.error(e);
		}
	}

	return false;
}
