sys_ui_script.script:
var x_424426_devops = x_424426_devops || {};
x_424426_devops.app_metadata = (function() {
	"use strict";

	/* eslint no-undef: "error" */
	x_424426_devops.loader.get('window', function ($window) {
		$window.addLoadEvent(function () {
			try {
				infuse($window);
			} catch (e) {
				$window.console.error('[DevOps+] failed to infuse metadata:', e);
			}
		});
	});

	function infuse($window) {
		const g_form = $window.g_form;
		if (!g_form || !$window.g_scratchpad || !$window.g_scratchpad.devops_plus) {
			return;
		}

		var element = $window.document.createElement('link');
		element.rel = 'stylesheet';
		element.href = '877dbaa697c886903c2d3f56f053afd8.cssdbx?v=1';
		$window.document.head.appendChild(element);

		// we only need to show fields which aren't visible on the form
		const fields = $window.g_scratchpad.devops_plus.fields.filter(f => {
			const ge = g_form.getElement(f.name);
			return !ge || !g_form.isFieldVisible(f.name);
		});

		if (fields.length == 0) {
			return;
		}

		const msgID = 'devops_plus_metadata_message';
		g_form.addInfoMessage('', msgID);
		const msgNode = $window.document.getElementById(msgID);
		if (!msgNode) {
			$window.console.warn('[DevOps+] Failed to add metadata information because message container could not be found!');
			return;
		}

		msgNode.appendChild('DevOps+ Hidden Metadata:');
		const table = $window.document.createElement('table');
		msgNode.appendChild(table);

		const header = $window.document.createElement('tr');
		table.appendChild(header);
		['Field', 'Value', ''].forEach(content => {
			const th = $window.document.createElement('th');
			header.appendChild(th);
			th.appendChild(content);
		});
		fields.forEach(f => {
			const tr = $window.document.createElement('tr');
			table.appendChild(tr);
			const labelTd = $window.document.createElement('td');
			tr.appendChild(labelTd);
			labelTd.appendChild(f.label);
			const valueTd = $window.document.createElement('td');
			tr.appendChild(valueTd);
			valueTd.appendChild(f.display_value);
			const actionTd = $window.document.createElement('td');
			tr.appendChild(actionTd);
			const button = $window.document.createElement('button');
			actionTd.appendChild(button);

			button.classList.add('btn', 'btn-default', 'btn-ref', 'icon', 'icon-edit');
			if (f.edit) {				
				button.addEventListener('click', function () {
					var gm = new $window.GlideModal('x_424426_devops_input_dialog');
					gm.setTitle('Set ' + f.label);
					gm.setPreference('label', f.label);
					gm.setPreference('value', f.display_value);
					gm.setPreference('onPromptComplete', function (value) {
						f.display_value = value;
						const ge = g_form.getElement(f.name);
						if (!ge) {
							// field is not on form
							var xhr = new $window.XMLHttpRequest();
							xhr.open('PUT', 'api/now/table/' + g_form.getTableName() + '/' + g_form.getUniqueValue(), true);
							xhr.setRequestHeader('X-UserToken', $window.g_ck);
							xhr.setRequestHeader('Accept', 'application/json');
							xhr.setRequestHeader('Content-Type', 'application/json');
							xhr.onload = function() {
								gm.destroy();
							};
							const data = {};
							data[f.name] = value;
							xhr.send(JSON.stringify(data));
						} else {
							// field is not visible
							g_form.setValue(f.name, value);
							gm.destroy();
						}
					});
					gm.render();
				});
				actionTd.appendChild(f.display_value);
			} else {
				button.setAttribute('disabled', '');
			}
		});
	}

	return {
		type: 'app_metadata'
	};
})();
