x_424426_devops_context_menu_item.client_script:
/* global $context, navigator, g_form, GlideAjax, GlideModalForm, devStudioAPI, g_navigation */
/* eslint no-undef: "error" */
(function ($context) {
	let gmf;
	const openInWindow = navigator.userAgent.indexOf('Mac OS X') > -1 ? $context.event.metaKey : $context.event.ctrlKey;
	const table = ((element) => {
		if (element) {
			if (element.type == 'table_name') return element.getValue();
			else if (element.type == 'glide_list') return element.reference;
			else if (element.type == 'reference') return element.reference;
		}
	})(g_form.getGlideUIElement(g_form.getTableName() == 'ni' ? g_form.resolveNameMap($context.FIELD) : $context.FIELD));

	if (table) {
		var ga = new GlideAjax('ContextMenuUtilClient').setScope('x_424426_devops');
		ga.addParam('sysparm_name', 'getTables');
		ga.addParam('table', table);
		ga.getXMLAnswer(function (result) {
			result = JSON.parse(result);
			if (result?.status != 'success') return;

			if (openInWindow) {
				g_navigation.openPopup('nav_to.do?uri=sys_db_object_list.do%3Fsysparm_query%3DnameIN' + result.tables);
				return;
			}

			const gmf = new GlideModalForm(
				'Hierarchy "' + table + '"',
				'sys_db_object_list'
			);
			gmf.addParm('sysparm_query', 'nameIN' + result.tables);
			// if in Studio, add the currently selected scoped so that
			// the translations are created in the currently selected application
			if (typeof devStudioAPI !== 'undefined') {
				gmf.addParm('sysparm_transaction_scope', devStudioAPI.appMetadata.APP_ID);
			}

			gmf.render();
		});
	}

})($context);

x_424426_devops_context_menu_item.condition_script:
/* global $context, Promise, g_form, GlideAjax */
/* eslint no-undef: "error" */
(function ($context) {
	const type = g_form.getGlideUIElement(
		g_form.getTableName() == 'ni'
		? g_form.resolveNameMap($context.FIELD)
		: $context.FIELD
	)?.type;
	return type == 'table_name'
		|| type == 'glide_list'
		|| (type == 'reference' && g_form.getTableName() != 'ni')
	;
})($context);
