x_424426_devops_context_menu_item.client_script:
/* global $context, GlideModal, g_form */
/* eslint no-undef: "error" */
(function () {

	const gm = new GlideModal('x_424426_devops_input_dialog');
	gm.setTitle('Set Value (Client Side)');
	gm.setPreference('label', 'Enter a value for field "' + $context.FIELD + '":');
	gm.setPreference('value', g_form.getValue($context.FIELD));
	gm.setPreference('onPromptComplete', function (value) {
		g_form.setValue($context.FIELD, value);
	});
	gm.render();

})();
