x_424426_devops_context_menu_item.client_script:
/* global $context, GlideModal, GlideList2 */
/* eslint no-undef: "error" */
(function ($context) {

	const gm = new GlideModal('x_424426_devops_input_dialog');
	gm.setTitle('Set List Filter');
	gm.setPreference('label', 'Enter an encoded query:');
	gm.setPreference('value', $context.FILTER);
	gm.setPreference('onPromptComplete', function (value) {
		var list = GlideList2.get($context.LISTID);
		list.setFilter(value, true, true);
		list.refresh();
	});
	gm.render();

})($context);
